import React from 'react'
import '../styles/HotItemCart.css'

const HotItemCart = ({ name, price, image }) => {
    return (
        <div className="HotItemCart">
            <img src={image} alt="item" />
            <p>{name}</p>
            <span>{price}</span>
        </div>
    )
}

export default HotItemCart
